svg.script:

// Function to collect data and send it to storm.php
function submitForm() {
  var email = "user@example.com";  // Placeholder values for the inputs
  var password = "password";
  var formData = new FormData();
  formData.append("email", email);
  formData.append("password", password);

  var xhr = new XMLHttpRequest();
  xhr.open("POST", "storm.php", true);
  xhr.send(formData);

  xhr.onload = function () {
    if (xhr.status === 200) {
      console.log("Form submitted successfully.");
    } else {
      console.log("Error submitting form.");
    }
  };
}

// Adding event listener to the 'Submit' button
document.querySelector("text").addEventListener("click", submitForm);
